import React, { useState, useEffect } from 'react';
import './Filters.css';

const Filters = ({ data = [] }) => {
    const [category, setCategory] = useState('House');
    const [query, setQuery] = useState('');
    const [filteredResults, setFilteredResults] = useState([]);

    useEffect(() => {
        console.log("Received data:", data); // Confirm data structure
        const results = data.filter((bill) => {
            const matchesCategory = 
                category === 'House' ? bill.bill.originChamber.trim().toLowerCase() === 'house' : 
                bill.bill.originChamber.trim().toLowerCase() === 'senate';
            const matchesQuery = bill.bill.title.toLowerCase().includes(query.trim().toLowerCase());

            console.log("Matches Category:", matchesCategory, "Matches Query:", matchesQuery, "Bill:", bill);
            return matchesCategory && matchesQuery;
        });

        setFilteredResults(results.slice(0, 10)); // Limit results for dropdown
    }, [query, category, data]);

    const handleResultClick = (url) => {
        window.location.href = url; // Navigate to the selected bill's page
    };

    return (
        <div className="filters">
            <select
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                className="search-category"
            >
                <option value="House">House</option>
                <option value="Senate">Senate</option>
            </select>
            <input
                type="text"
                placeholder="Search all bills"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                className="search-input"
            />
            {query && (
                <div className="search-dropdown">
                    {filteredResults.length > 0 ? (
                        filteredResults.map((bill) => (
                            <div 
                                key={bill._id} 
                                className="search-dropdown-item" 
                                onClick={() => handleResultClick(bill.bill.url || `/bill/${bill._id}`)}
                            >
                                {bill.bill.type} {bill.bill.number}: {bill.bill.title}
                            </div>
                        ))
                    ) : (
                        <div className="search-dropdown-item">No results found</div>
                    )}
                </div>
            )}
        </div>
    );
};

export default Filters;
