    const { id } = useParams();
    const bill = data[id];

    return (
        <div className="bill-details">
            {/* <h2>{bill.bill?.bill?.title || "Title Not Available"}</h2> */}
            {/* <p className="congress">{bill.bill?.bill?.congress}</p> */}
