import React, { useState, useEffect } from 'react';
import './Filters.css';

const Filters = ({ data = [] }) => {
    const [query, setQuery] = useState('');
    const [filteredResults, setFilteredResults] = useState([]);

    useEffect(() => {
        // Filter data based on search query
        const results = data.filter((item) => {
            const title = item.bill.bill.title?.toLowerCase();
            const billNumber = `${item.bill.bill.type}.${item.bill.bill.number}`.toLowerCase();
            const matchesQuery = title.includes(query.toLowerCase()) || billNumber.includes(query.toLowerCase());
            return matchesQuery;
        });

        setFilteredResults(results.slice(0, 10)); // Limit results for dropdown
    }, [query, data]);

    const handleResultClick = (url) => {
        window.location.href = url; // Navigate to the selected bill's page
    };

    return (
        <div className="filters">
            <input
                type="text"
                placeholder="Search all bills"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                className="search-input"
            />
            {query && (
                <div className="search-dropdown">
                    {filteredResults.length > 0 ? (
                        filteredResults.map((item) => (
                            <div 
                                key={item._id} 
                                className="search-dropdown-item" 
                                onClick={() => handleResultClick(item.bill.bill.url || `/bill/${item._id}`)}
                            >
                                {item.bill.bill.type}.{item.bill.bill.number}: {item.bill.bill.title}
                            </div>
                        ))
                    ) : (
                        <div className="search-dropdown-item">No results found</div>
                    )}
                </div>
            )}
        </div>
    );
};

export default Filters;
