const Filters = ({ data = [] }) => {
    const [category, setCategory] = useState('House');
    const [query, setQuery] = useState('');
    const [filteredResults, setFilteredResults] = useState([]);

    useEffect(() => {
        // Filter bills based on category and query input
        const results = data.filter((bill) => {
            const matchesCategory = 
                category === 'House' ? bill.bill.originChamber === 'House' : 
                bill.bill.originChamber === 'Senate';
            const matchesQuery = bill.bill.title.toLowerCase().includes(query.toLowerCase());
            return matchesCategory && matchesQuery;
        });

        setFilteredResults(results);
    }, [query, category, data]);

    const handleResultClick = (url) => {
        window.location.href = url; // Navigate to the selected bill's page
    };

    return (
        <div className="filters">
            <select
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                className="search-category"
            >
                <option value="House">House</option>
                <option value="Senate">Senate</option>
            </select>
            <input
                type="text"
                placeholder="Search all bills"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                className="search-input"
            />
            {query && (
                <div className="search-dropdown">
                    {filteredResults.map((bill) => (
                        <div 
                            key={bill._id} 
                            className="search-dropdown-item" 
                            onClick={() => handleResultClick(bill.bill.url || `/bill/${bill._id}`)}
                        >
                            {bill.bill.type} {bill.bill.number}: {bill.bill.title}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};
