import React, { useState, useEffect } from 'react';
import './Filter.css';
import { useNavigate } from 'react-router-dom';
import { filterBills } from './AllBills'; // Import the filterBills function

const Filter = ({ data = [] }) => {
    const [query, setQuery] = useState('');
    const [filteredResults, setFilteredResults] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const results = filterBills(data, query); // Use the imported filterBills function
        setFilteredResults(results.slice(0, 10)); // Limit to 10 results for dropdown
    }, [query, data]);

    const handleResultClick = (id) => {
        navigate(`/bill/${id}`);
    };

    return (
        <div className="filter">
            <input
                type="text"
                placeholder="Search bills by title or number"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                className="filter-input"
            />

            {query && filteredResults.length > 0 && (
                <div className="filter-dropdown">
                    {filteredResults.map((item) => (
                        <div
                            key={item._id}
                            className="filter-dropdown-item"
                            onClick={() => handleResultClick(item._id)}
                        >
                            {item.bill.bill.type}.{item.bill.bill.number}: {item.bill.bill.title}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Filter;
