// Footer.js
import React, { useState, useEffect } from 'react';

const Footer = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);

    // useEffect(() => {
    //     // Load the Mailchimp validation script after the component mounts
    //     const script = document.createElement("script");
    //     script.src = "//s3.amazonaws.com/downloads.mailchimp.com/js/mc-validate.js";
    //     script.async = true;
    //     document.body.appendChild(script);

    //     return () => {
    //         document.body.removeChild(script);
    //     };
    // }, []);

    const toggleModal = () => {
        setIsModalOpen(!isModalOpen);
    };

    return (
        <footer className="footer">
            <button onClick={toggleModal} className="signup-button">
                Sign Up for Alerts
            </button>

            {isModalOpen && (
                <div className="modal-overlay" onClick={toggleModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <button className="close-button" onClick={toggleModal}>âœ–</button>
                        <div id="mc_embed_shell">
                            <link
                                href="//cdn-images.mailchimp.com/embedcode/classic-061523.css"
                                rel="stylesheet"
                                type="text/css"
                            />
                            <div id="mc_embed_signup" style={{ background: "#fff", font: "14px Helvetica,Arial,sans-serif", width: "600px" }}>
                                <form
                                    action="https://hamilton.us17.list-manage.com/subscribe/post?u=5655c218737b1c2830a7677a1&amp;id=568adfc2bd&amp;f_id=0058c3e1f0"
                                    method="post"
                                    id="mc-embedded-subscribe-form"
                                    name="mc-embedded-subscribe-form"
                                    className="validate"
                                    target="_blank"
                                >
                                    <div id="mc_embed_signup_scroll">
                                        <h2>Sign Up for Alerts</h2>
                                        <div className="indicates-required">
                                            <span className="asterisk">*</span> indicates required
                                        </div>
                                        <div className="mc-field-group">
                                            <label htmlFor="mce-EMAIL">
                                                Email Address <span className="asterisk">*</span>
                                            </label>
                                            <input
                                                type="email"
                                                name="EMAIL"
                                                className="required email"
                                                id="mce-EMAIL"
                                                required
                                            />
                                        </div>
                                        <div id="mce-responses" className="clear foot">
                                            <div className="response" id="mce-error-response" style={{ display: "none" }}></div>
                                            <div className="response" id="mce-success-response" style={{ display: "none" }}></div>
                                        </div>
                                        <div aria-hidden="true" style={{ position: "absolute", left: "-5000px" }}>
                                            <input
                                                type="text"
                                                name="b_5655c218737b1c2830a7677a1_568adfc2bd"
                                                tabIndex="-1"
                                                value=""
                                            />
                                        </div>
                                        <div className="optionalParent">
                                            <div className="clear foot">
                                                <input
                                                    type="submit"
                                                    name="subscribe"
                                                    id="mc-embedded-subscribe"
                                                    className="button"
                                                    value="Subscribe"
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </footer>
    );
};

export default Footer;
