import React, { useState, useEffect } from 'react';
import './AllBills.css';
import { Link } from 'react-router-dom';

export const filterBills = (data, query) => {
    if (!data || !query) return [];
    return data.filter((item) => {
        const title = item.bill.bill.title?.toLowerCase();
        const billNumber = `${item.bill.bill.type}.${item.bill.bill.number}`.toLowerCase();
        return title.includes(query.toLowerCase()) || billNumber.includes(query.toLowerCase());
    });
};

const AllBills = ({ data }) => {
    const [query, setQuery] = useState('');
    const [filteredData, setFilteredData] = useState([]);

    useEffect(() => {
        const results = filterBills(data, query); // Use filterBills function
        setFilteredData(results);
    }, [query, data]);

    // Render the table as usual
    return (
        <div>
            <h2 className="all-bills-title">All Bill Updates</h2>
            <input
                type="text"
                placeholder="Search bills by title or number"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                className="search-input"
            />
            <div className="table-container">
                <table className="bill-table">
                    <thead>
                        <tr>
                            <th>Bill Number</th>
                            <th>Title (click for more info)</th>
                            <th>Congress Year</th>
                            <th>Update</th>
                            <th>Update Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredData.length > 0 ? (
                            filteredData.map((item) => (
                                <tr key={item._id}>
                                    <td>{item.bill.bill.type}.{item.bill.bill.number}</td>
                                    <td>
                                        <Link to={`/bill/${item._id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                                            {item.bill.bill.title}
                                        </Link>
                                    </td>
                                    <td>{item.bill.bill.congress}</td>
                                    <td>{item.bill.actionDesc}</td>
                                    <td>{item.bill.actionDate}</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="5">No results found</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default AllBills;
