import React, { useState, useEffect } from 'react';
import './Filters.css';
import { Link, useNavigate } from 'react-router-dom';

const Filter = ({ data }) => {
    const [query, setQuery] = useState('');
    const [filteredData, setFilteredData] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Filter data based on search query
        const results = data.filter((item) => {
            const title = item.bill.bill.title?.toLowerCase();
            const billNumber = `${item.bill.bill.type}.${item.bill.bill.number}`.toLowerCase();
            const matchesQuery = title.includes(query.toLowerCase()) || billNumber.includes(query.toLowerCase());
            return matchesQuery;
        });

        setFilteredData(results);
    }, [query, data]);

    const handleResultClick = (id) => {
        navigate(`/bill/${id}`);
    };

    return (
        <div className="filter">
            <input
                type="text"
                placeholder="Search bills by title or number"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                className="filter-input"
            />

            {/* Only display dropdown if there are filtered results */}
            {query && filteredData.length > 0 && (
                <div className="filter-dropdown">
                    {filteredData.map((item) => (
                        <div
                            key={item._id}
                            className="filter-dropdown-item"
                            onClick={() => handleResultClick(item._id)}
                        >
                            {item.bill.bill.type}.{item.bill.bill.number}: {item.bill.bill.title}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );

};

export default Filter;
