import React, { useState, useEffect } from 'react';
import './Filters.css';
import { useNavigate } from 'react-router-dom';

const Filter = ({ data = [] }) => { // Default value for data in case it's undefined
    const [query, setQuery] = useState('');
    const [filteredResults, setFilteredResults] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Filter data only if query is not empty
        if (query && data.length > 0) {
            const results = data.filter((item) => {
                const title = item.bill.bill.title?.toLowerCase();
                const billNumber = `${item.bill.bill.type}.${item.bill.bill.number}`.toLowerCase();
                return title.includes(query.toLowerCase()) || billNumber.includes(query.toLowerCase());
            });
            setFilteredResults(results.slice(0, 10)); // Limit to 10 results for dropdown
        } else {
            setFilteredResults([]); // Clear results if query is empty
        }
    }, [query, data]);

    const handleResultClick = (id) => {
        navigate(`/bill/${id}`);
    };

    return (
        <div className="filter">
            <input
                type="text"
                placeholder="Search bills by title or number"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                className="filter-input"
            />

            {filteredResults.length > 0 && (
                <div className="filter-dropdown">
                    {filteredResults.map((item) => (
                        <div
                            key={item._id}
                            className="filter-dropdown-item"
                            onClick={() => handleResultClick(item._id)}
                        >
                            {item.bill.bill.type}.{item.bill.bill.number}: {item.bill.bill.title}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Filter;
